package com.kims.hackathon.view.recyclerview;

import static com.kims.hackathon.view.fragment.CategoryFragment.*;

import android.content.Context;
import android.graphics.Rect;
import android.util.AttributeSet;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ImageView;
import android.widget.TextView;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import com.bumptech.glide.Glide;
import com.kims.hackathon.R;
import com.kims.hackathon.client.bucket.CategoryHocket;
import com.kims.hackathon.view.SimpleHeartView;

import java.util.ArrayList;
import java.util.List;

public class CategoryHocketRecyclerView extends RecyclerView {

    private Context context;
    private Category category;

    public CategoryHocketRecyclerView(@NonNull Context context) {
        super(context);
        this.context = context;
        initView();
    }

    public CategoryHocketRecyclerView(@NonNull Context context, @Nullable AttributeSet attrs) {
        super(context, attrs);
        this.context = context;
        initView();
    }

    private void initView() {
        setLayoutManager(getHorizontalLayoutManager());
        addItemDecoration(new CategoryHocketItemDecoration(50));
        setAdapter(new CategoryHocketAdapter(context, getItems()));
    }

    private List<CategoryHocket> getItems() {
        List<CategoryHocket> dummyHockets = new ArrayList<>();
        CategoryHocket categoryHocket = new CategoryHocket();
        categoryHocket.setLikes(10);
        categoryHocket.setTitle("서핑 도전하기");
        categoryHocket.setImageUrl("https://health.chosun.com/site/data/img_dir/2016/08/02/2016080201049_0.jpg");

        CategoryHocket categoryHocket2 = new CategoryHocket();
        categoryHocket2.setLikes(15);
        categoryHocket2.setTitle("PT 등록하기");
        categoryHocket2.setImageUrl("data:image/jpeg;base64,");

        CategoryHocket categoryHocket3 = new CategoryHocket();
        categoryHocket3.setLikes(5);
        categoryHocket3.setTitle("암벽 등반하기");
        categoryHocket3.setImageUrl("https://lh3.googleusercontent.com/proxy/VrRyeYbfZ5fhvmkuf-SUsdPqL_drelJG3010OShj1GgZngXlNAfXK9xsKQoZHMtG4WR-Xf4QiQgD6EJ0eiHUA0u-cvLYJjoQcGJ4bHjoGcNA-GQAC5nwYUStVdYV3-Q");

        dummyHockets.add(categoryHocket);
        dummyHockets.add(categoryHocket2);
        dummyHockets.add(categoryHocket3);
        return dummyHockets;
    }

    private LayoutManager getHorizontalLayoutManager() {
        return new LinearLayoutManager(context, LinearLayoutManager.HORIZONTAL, false);
    }

    private static class CategoryHocketAdapter extends Adapter<CategoryHocketViewHolder> {

        private Context context;
        private List<CategoryHocket> categoryHockets;

        private CategoryHocketAdapter(Context context, List<CategoryHocket> items) {
            this.context = context;
            this.categoryHockets = items;
        }

        @NonNull
        @Override
        public CategoryHocketViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {
            View view = LayoutInflater.from(context).inflate(R.layout.category_hocket, parent, false);
            return new CategoryHocketViewHolder(view);
        }

        @Override
        public void onBindViewHolder(@NonNull CategoryHocketViewHolder holder, int position) {
            CategoryHocket categoryHocket = categoryHockets.get(position);
            String title = categoryHocket.getTitle();
            String imageUrl = categoryHocket.getImageUrl();
            int likes = categoryHocket.getLikes();

            holder.titleView.setText(title);
            holder.simpleHeartView.setLikeNumber(likes);
            Glide.with(context).load(imageUrl).into(holder.imageView);
        }

        @Override
        public int getItemCount() {
            return categoryHockets.size();
        }
    }

    private static class CategoryHocketViewHolder extends ViewHolder {

        private ImageView imageView;
        private TextView titleView;
        private SimpleHeartView simpleHeartView;

        public CategoryHocketViewHolder(@NonNull View itemView) {
            super(itemView);
            this.imageView = itemView.findViewById(R.id.category_hocket_image);
            this.titleView = itemView.findViewById(R.id.category_hocket_title);
            this.simpleHeartView = itemView.findViewById(R.id.category_hocket_heart);
        }
    }

    private static class CategoryHocketItemDecoration extends ItemDecoration {
        private final int marginRight;

        CategoryHocketItemDecoration(int marginRight) {
            this.marginRight = marginRight;
        }

        @Override
        public void getItemOffsets(@NonNull Rect outRect, @NonNull View view, @NonNull RecyclerView parent, @NonNull State state) {
            super.getItemOffsets(outRect, view, parent, state);
            outRect.right = marginRight;
        }
    }
}
